#thresholds for various merges (I'll explain this someday... maybe)
#the higher the thresholds, the more the image will be compressed.
color_merge_threshold = 1250
temporal_merge_threshold = 1300
neighbor_merge_threshold = 530

#the bitdepth of the colors of singletons. (I'll explain this someday... maybe)
singleton_bit_depth = 4

#take into account the frequency of colors when merging them. (Advice: Just leave this as True. False seems to compress worse, and has slightly worse image quality)
use_good_averaging = True

#utility function
def saneIndex(listy,element):
	if(len(listy) == 0):
		return(-1)
	return(listy.index(element) if element in listy else -1)


