#reads an image and returns an array of pixels from a section.
def getTileArray(tx,ty,image):
	tileArr = []
	for i in range(5):
		for j in range(5):
			#the resized sometimes image discards the alpha channel, for whatever reason.
			#b, g, r, a = resizedPix[tx*5+j,ty*5+i]
			b, g, r = pictures[image][tx*5+i,ty*5+j]
			
			tileArr.append((r,g,b))
	
	return(tileArr)

#how far away are two colors? Low values mean the two colors are pretty similar, high values mean they're not.
#returns the sum of the squared distance of each channel.
def colorDistance(color1,color2):
	cr,cg,cb = color1
	cr = int(cr)
	cg = int(cg)
	cb = int(cb)
	
	pr, pg, pb = color2
	pr = int(pr)
	pg = int(pg)
	pb = int(pb)
	
	dist = (cr-pr,cg-pg,cb-pb)
	dist = dist[0]*dist[0] + dist[1]*dist[1] + dist[2]*dist[2]
	
	return(dist)

#takes two colors (and their frequencies) and averages them.
def averageColor(color1,color2,freq1,freq2):
	
	#if the use_good_averaging flag is true, we take frequency into account when averaging colors (the more frequent color is weighted more heavily)
	if(not use_good_averaging):
		freq1 = 1
		freq2 = 1
	
	cr,cg,cb = color1
	cr = int(cr)*freq1
	cg = int(cg)*freq1
	cb = int(cb)*freq1
	
	pr, pg, pb = color2
	pr = int(pr)*freq2
	pg = int(pg)*freq2
	pb = int(pb)*freq2
	
	total = freq1+freq2
	
	avg = (cr+pr,cg+pg,cb+pb)
	avg = (int(avg[0]/total),int(avg[1]/total),int(avg[2]/total))
	
	return(avg)

aggressiveColorMerges = 0

#we need to format the bitmask here...
singleton_bit_mask = 255 << (8-singleton_bit_depth)

#...and the number that lets us do efficient rounding
singletonRoundNum = int((2 ** (8-singleton_bit_depth))/2)

def getColorPalette(arr):
	global aggressiveColorMerges
	global singleton_bit_mask
	
	#each element is a list of: [color,frequency]
	colorArr = []
	
	for i in arr:
		index = saneIndex(colorArr,i)
		
		if(index == -1):
			colorArr.append([i,1])
		else:
			colorArr[index][1] += 1
	
	#prune list to only 10 colors, because a puzzlescript tile can only have 10.
	while(len(colorArr) > 10):
		
		dist = 999999999
		closestA = -1
		closestB = -1
		
		for b in range(len(colorArr)):
			for a in range(b):
				thisDist = colorDistance(colorArr[a][0],colorArr[b][0])
				if(dist > thisDist):
					dist = thisDist
					closestA = a
					closestB = b
		
		
		colorA = colorArr[closestA]
		colorB = colorArr[closestB]
		colorArr.pop(closestB)
		colorArr.pop(closestA)
		colorArr.append([averageColor(colorA[0],colorB[0],colorA[1],colorB[1]),colorA[1]+colorB[1]])
	
	#if any colors are close enough, merge them regardless
	threshold = color_merge_threshold
	
	prevArr = copy.deepcopy(colorArr)
	
	changed = True
	while(changed):
		
		changed = False
		
		for b in range(len(colorArr)):
			for a in range(b):
				
				if(a >= b or b >= len(colorArr)):
					continue
				
				thisDist = colorDistance(colorArr[a][0],colorArr[b][0])
				if(color_merge_threshold >= thisDist):
					colorA = colorArr[a]
					colorB = colorArr[b]
					colorArr.pop(b)
					colorArr.pop(a)
					#colorArr.append(averageColor(colorA,colorB))
					colorArr.append([averageColor(colorA[0],colorB[0],colorA[1],colorB[1]),colorA[1]+colorB[1]])
					changed = True
	
	#If our thourough merging couldn't result in having a palette of <= 1 colors, it's probably not going to be optimized later, so me might as well use all the colors we can.
	if(len(colorArr) > 1):
		colorArr = prevArr
	elif(debug and len(prevArr) != 1):
		aggressiveColorMerges += 1
	
	
	#we don't need frequency anymore
	for i in range(len(colorArr)):
		colorArr[i] = colorArr[i][0]
	
	
	#reduce the color depth of singletons, in the hope that they can be merged
	if(len(colorArr) == 1):
		r,g,b = colorArr[i]
		
		r = (r+singletonRoundNum) & singleton_bit_mask
		g = (g+singletonRoundNum) & singleton_bit_mask
		b = (b+singletonRoundNum) & singleton_bit_mask
		
		r = min(255,r)
		g = min(255,g)
		b = min(255,b)
		
		colorArr[i] = (r,g,b)
	
	#todo: sort color list for merging pourpouses
	return(colorArr)

#there's a good chance that our color palette won't exactly line up with the colors in the original tile.
#so, we find the color that fits best.
def getClosestColor(color,palette):
	closest = -1
	dist = 999999999
	
	for i in range(len(palette)):
		
		thisDist = colorDistance(color,palette[i])
		
		if(dist > thisDist):
			dist = thisDist
			closest = i
	
	return(closest)

#assigning the closest color en masse.
def assignColorPalette(tile,color):
	tileArr = []
	for i in range(len(tile)):
		index = saneIndex(color,tile[i])
		if(index != -1):
			tileArr.append(index)
		else:
			tileArr.append(getClosestColor(tile[i],color))
	
	return(tileArr)

#how different are two tiles?
#low values mean they're similar, high values mean they're not.
def calcTileDifference(tile1,tile2):
	
	diff = 0.0
	
	for i in range(25):
		
		c1 = tile1[1][tile1[0][i]]
		c2 = tile2[1][tile2[0][i]]
		distance = colorDistance(c1,c2)
		
		diff += (distance/25.0)
		
	
	return(diff)

#puzzlescript uses a hexadecimal format for colors. It's the same format as HTML colors, hence the name.
#so, here he convert RGB to html color.
def htmlColor(r,g,b):
	
	sr = hex(r)
	sg = hex(g)
	sb = hex(b)
	
	sr = sr[2:]
	sg = sg[2:]
	sb = sb[2:]
	
	sr = ("0" + sr if len(sr) == 1 else sr)
	sg = ("0" + sg if len(sg) == 1 else sg)
	sb = ("0" + sb if len(sb) == 1 else sb)
	
	return("#" + sr + sg + sb)

#this function will format our tiles in a way that puzzlescript understands.
def printTile(tile,palette,name):
	stringy = name
	stringy += "\n"
	for i in palette:
		r, g, b = i
		curPixel = htmlColor(r,g,b)
		stringy += curPixel + " "
	stringy += "\n"
	if(len(palette) > 1):
		for i in range(len(tile)):
			stringy += str(tile[i])
			if(i % 5 == 4):
				stringy += "\n"
	stringy += "\n"
	
	return(stringy)



globalTiles = []

tileLayouts = []

tileHash = {}

singletons = 0
temporalMerges = 0
neighborMerges = 0

#this is where the bulk of the work happens.
#It takes the pictures and turns them into grids of tiles.
def genImage():
	global singletons
	global temporalMerges
	global neighborMerges
	
	for pix in range(len(pictures)):
		
		tileLayouts.append([])
		current = len(tileLayouts)-1
		
		for i in range(tileH):
			for j in range(tileW):
				
				nextLoc = len(tileLayouts[current])
				
				tilePixels = getTileArray(i,j,pix)
				palette = getColorPalette(tilePixels)
				adjTile = assignColorPalette(tilePixels,palette)
				
				tile = (tuple(adjTile),tuple(palette))
				
				if(tile not in tileHash):
					
					#see if we can use the tile from the previous frame
					if(current != 0):
						
						prevTile = globalTiles[tileLayouts[current-1][nextLoc]]
						
						#if(len(tile[1]) > 1 and len(prevTile[1]) > 1):
						if(True):
							difference = calcTileDifference(tile,prevTile)
							if(difference < temporal_merge_threshold):
								tileLayouts[current].append(tileLayouts[current-1][nextLoc])
								temporalMerges += 1
								continue
					
					#see if we can use tile above us:
					if(i != 0):
						
						aboveTile = globalTiles[tileLayouts[current][nextLoc-tileW]]
						
						difference = calcTileDifference(tile,aboveTile)
						if(len(tile[1]) == 1):
							difference *= 1.3
						if(difference < neighbor_merge_threshold):
							tileLayouts[current].append(tileLayouts[current][nextLoc-tileW])
							neighborMerges += 1
							continue
					
					#see if we can use tile to left of us:
					if(j != 0):
						
						sideTile = globalTiles[tileLayouts[current][nextLoc-1]]
						
						difference = calcTileDifference(tile,sideTile)
						if(len(tile[1]) == 1):
							difference *= 1.3
						if(difference < neighbor_merge_threshold):
							tileLayouts[current].append(tileLayouts[current][nextLoc-1])
							neighborMerges += 1
							continue
					
					#add and use new tile
					globalTiles.append(tile)
					tileLayouts[current].append(len(globalTiles)-1)
					tileHash[tile] = len(globalTiles)-1
					
					if(len(tile[1]) == 1):
						singletons += 1
					
				else:
					#we already generated an identical tile, so use that
					tileLayouts[current].append(tileHash[tile])

genImage()

